function entry() {
    let fileInput = document.getElementById("file-upload");
    let files = fileInput.files;
    let final_images = []; // Define a local list to store final images

    for (let i = 0; i < files.length; i++) {
        let file = files[i]; 

        modify_image(file, document.getElementById("checkbox").checked, function(final) {
            final_images.push(final); // Append the final image to the list

            // If all images have been processed, combine and display them
            if (final_images.length === files.length) {
                combine_images_centered(final_images, function(display_image) {
                    console.log(display_image.width, display_image.height);
                    displayImage(display_image);
                });
            }
        });
    }
}

function combine_images_centered2(images,callback) {

    console.log(images.length);

    // Calculate number of rows and columns for the grid
    let num_images = images.length;
    let num_cols = Math.ceil(Math.sqrt(num_images));
    let num_rows = Math.ceil(num_images / num_cols);

    // Calculate width and height of each image
    let image_width = images[0].width;
    let image_height = images[0].height;

    // Calculate total width and height of the combined grid
    let total_width = num_cols * image_width;
    let total_height = num_rows * image_height;

    // Create a canvas for the combined image
    let combinedCanvas = document.createElement('canvas');
    let ctx = combinedCanvas.getContext('2d');

    // Set canvas dimensions to fit the combined image
    combinedCanvas.width = total_width;
    combinedCanvas.height = total_height;

    // Calculate left offset to center the last row if needed
    let left_offset_last_row = Math.floor((total_width - (num_cols - (num_cols * num_rows - num_images)) * image_width) / 2);

    // Stitch images together in a grid-like structure
    for (let i = 0; i < num_images; i++) {
        let col = i % num_cols;
        let row = Math.floor(i / num_cols);

        let left_offset = 0

        // if its the last row
        if (row == num_rows - 1) {
            left_offset = left_offset_last_row;
        }

        // Calculate x-coordinate for the image
        let x = col * image_width + left_offset;

        // Calculate y-coordinate for the image
        let y = row * image_height;

        ctx.drawImage(images[i], x, y);
    }

    // Convert combined canvas to image
    let combinedImage = new Image();
    combinedImage.src = combinedCanvas.toDataURL();

    console.log(combinedImage.width, combinedImage.height);

    callback(combinedImage);
}

function combine_images_centered(images, callback) {
    console.log(images.length);

    // Variable to track the number of images that have loaded
    let imagesLoaded = 0;

    // Function to check if all images have loaded
    function checkAllImagesLoaded() {
        if (imagesLoaded === images.length) {
            // All images have loaded, proceed with combining
            combine();
        }
    }

    // Function to combine images once all are loaded
    function combine() {
        // Once all images are loaded, proceed with combining
        console.log(imagesLoaded + " images loaded");
        console.log("two images dims: ", images[0].width, images[0].height, images[1].width, images[1].height);

        // Calculate number of rows and columns for the grid
        let num_images = images.length;
        let num_cols = Math.ceil(Math.sqrt(num_images));
        let num_rows = Math.ceil(num_images / num_cols);

        // Calculate width and height of each image
        let image_width = images[0].naturalWidth || images[0].width;
        let image_height = images[0].naturalHeight || images[0].height;

        // Calculate total width and height of the combined grid
        let total_width = num_cols * image_width;
        let total_height = num_rows * image_height;

        // Create a canvas for the combined image
        let combinedCanvas = document.createElement('canvas');
        let ctx = combinedCanvas.getContext('2d');

        // Set canvas dimensions to fit the combined image
        combinedCanvas.width = total_width;
        combinedCanvas.height = total_height;

        console.log("canvas dims: ", combinedCanvas.width, combinedCanvas.height);

        // Calculate left offset to center the last row if needed
        let left_offset_last_row = Math.floor((total_width - (num_cols - (num_cols * num_rows - num_images)) * image_width) / 2);

        // Stitch images together in a grid-like structure
        for (let i = 0; i < num_images; i++) {
            let col = i % num_cols;
            let row = Math.floor(i / num_cols);

            let left_offset = 0;

            // if it's the last row
            if (row == num_rows - 1) {
                left_offset = left_offset_last_row;
            }

            // Calculate x-coordinate for the image
            let x = col * image_width + left_offset;

            // Calculate y-coordinate for the image
            let y = row * image_height;

            ctx.drawImage(images[i], x, y);
        }

        // Convert combined canvas to image
        let combinedImage = new Image();
        combinedImage.src = combinedCanvas.toDataURL();

        console.log(combinedImage.width, combinedImage.height);

        callback(combinedImage);
    }

    // Function to handle image load event
    function imageLoaded() {
        // Increment the counter for loaded images
        imagesLoaded++;

        // Check if all images have loaded
        checkAllImagesLoaded();
    }

    // Check if all images are already loaded (in case they are cached)
    images.forEach(function(image) {
        if (image.complete) {
            // Image is already loaded, handle it immediately
            imageLoaded();
        } else {
            // Wait for the image to load
            image.onload = imageLoaded;
        }
    });
}


function watermark(image) {
    // Create a canvas element
    let canvas = document.createElement('canvas');
    let ctx = canvas.getContext('2d');

    // Set canvas dimensions to match the input image
    canvas.width = image.width;
    canvas.height = image.height;

    // Draw the input image onto the canvas
    ctx.drawImage(image, 0, 0);

    // Define the placeholder watermark as a array
    let watermark_arr = get_watermark_array();
    let watermark_width = 336;
    let imgData  = ctx.getImageData(0, 0, canvas.width, canvas.height); 
    let data = imgData.data;

    let watermark_idx = 0;

    for(let i = 0; i < data.length; i += 4) {
        for (let j = 0; j < 3; j++){
            if (watermark_arr[watermark_idx] != 1){
                watermark_arr[watermark_idx] = 0.7;
            }
            data[i+j] = Math.floor(data[i+j] * watermark_arr[watermark_idx]);
        } 
        
        watermark_idx += 1;

        if (watermark_idx%watermark_width == 0){
            // go to the next row
            i += image.width * 4 - watermark_width * 4;
        }

        if (watermark_idx >= watermark_arr.length) {
            break;
        }


    }

    ctx.putImageData(imgData, 0, 0);


    // Return the modified image as a data URL with the correct MIME type
    return canvas.toDataURL(); // No need to specify MIME type here
}

