function combine_images_left_aligned(images, callback) {
    // Calculate number of rows and columns for the grid
    let num_images = images.length;
    let num_cols = Math.ceil(Math.sqrt(num_images));
    let num_rows = Math.ceil(num_images / num_cols);

    // Calculate width and height of each image
    let image_width = images[0].width;
    let image_height = images[0].height;

    // Create a canvas for the combined image
    let combinedCanvas = document.createElement('canvas');
    let ctx = combinedCanvas.getContext('2d');

    // Set canvas dimensions to fit the combined image
    combinedCanvas.width = num_cols * image_width;
    combinedCanvas.height = num_rows * image_height;

    // Stitch images together in a grid-like structure
    for (let i = 0; i < num_images; i++) {
        let col = i % num_cols;
        let row = Math.floor(i / num_cols);
        ctx.drawImage(images[i], col * image_width, row * image_height);
    }

    // Convert combined canvas to image
    let combinedImage = new Image();
    combinedImage.src = combinedCanvas.toDataURL();

    callback(combinedImage);
}


function displayImage(image) {
    // Create an img element
    let imgElement = document.createElement('img');
    
    // Set src attribute to the data URL of the modified image
    imgElement.src = watermark(image);

    // Set CSS properties to scale down the image while preserving aspect ratio
    imgElement.style.maxWidth = '90%';
    imgElement.style.maxHeight = '90%';
    imgElement.style.width = 'auto';
    imgElement.style.height = 'auto';

    // Clear previous content of the right section
    let rightSection = document.querySelector('.right-section');
    rightSection.innerHTML = '';

    // Append the img element to the right section
    rightSection.appendChild(imgElement);

    // Create a download button
    let downloadButton = document.createElement('button');
    downloadButton.innerText = 'Download Image';
    downloadButton.classList.add('download-button');

    // Append the download button to the right section
    rightSection.appendChild(downloadButton);

    // Attach event listener to the download button
    downloadButton.addEventListener('click', function() {
        downloadImage(imgElement.src);
    });
}


function downloadImage(imageUrl) {
    // Create a temporary anchor element
    let link = document.createElement('a');
    link.href = imageUrl;

    // Set the download attribute to ensure browser downloads the image instead of navigating to it
    link.download = 'image.png';

    // Programmatically click the anchor element to trigger the download
    link.click();
}

function convert_to_img(imageFile, callback) {
    let image = new Image();
    image.onload = function() {
        callback(image);
    };

    image.src = URL.createObjectURL(imageFile);
}

function modify_image(imageFile, checkboxValue, callback) {
    convert_to_img(imageFile, function(image) {
        let editedImage = image;

        editedImage = crop_image(editedImage, checkboxValue);

        if (checkboxValue) {
            editedImage = change_color(editedImage);
        }

        callback(editedImage); 
    });
}

function change_color(image) {
    // Add your code here to change the color of the image
    // This function should return the modified image
    return image; // Placeholder, modify as needed
}

function crop_image(image, checkboxValue) {
    let canvas = document.createElement('canvas');
    let ctx = canvas.getContext('2d');

    canvas.width = image.width;
    canvas.height = image.height;

    ctx.drawImage(image, 0, 0);

    let h_modifier = image.height / 1080;
    let w_modifier = image.width / 1920;
    let left_crop = left_marg * w_modifier;
    let top_crop = top_marg * h_modifier;
    let right_crop = image.width - right_marg * w_modifier;
    let bottom_crop = image.height - bottom_marg * h_modifier;

    let croppedCanvas = document.createElement('canvas');
    let croppedCtx = croppedCanvas.getContext('2d');

    // Set cropped canvas dimensions
    croppedCanvas.width = right_crop - left_crop;
    croppedCanvas.height = bottom_crop - top_crop;

    croppedCtx.drawImage(canvas, left_crop, top_crop, right_crop - left_crop, bottom_crop - top_crop, 0, 0, right_crop - left_crop, bottom_crop - top_crop);

    let croppedImage = new Image();
    croppedImage.src = croppedCanvas.toDataURL();

    return croppedImage;
}



